        </svg>`,
          methodInfo: {
            text: this.$t('Will send today')
          },
          price: {
            base: '35',
            free: false
          }
        },
        justin_to_department: {
          icon: `<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="24" height="24" viewBox="0 0 24 24" fill="none">
          <rect x="2" y="2" width="20" height="20" fill="url(#pattern_justin)"/>
          <defs>
            <pattern id="pattern_justin" patternContentUnits="objectBoundingBox" width="1" height="1">
              <use xlink:href="#image_justin" transform="scale(0.000925926)"/>
            </pattern>
            <image id="image_justin" width="1080" height="1080" xlink:href="data:image/png;base64,"/>
          </defs>
        </svg>`,
          methodInfo: {
            text: this.$t('Will send today')
          },
          price: {
            base: '45',
            free: false
          }
        }
      }
    }
  },
  computed: {
    ...mapGetters({
      getCurrentProduct: 'product/getCurrentProduct'
    }),
    ...mapState({
      shops: (state) => state.checkoutPage.shops,
      city: (state) => state.ui.city
    }),
    availableShops () {
      return this.shops.filter(it => {
        return this.getCurrentProduct.msi_sources.some(store => store.source_code.includes(it.source_code) && store.salable_quantity)
      })
    },
    shopsDescription () {
      return this.$tc('Available in {count} shops', this.availableShops.length)
    },
    storeView () {
      return currentStoreView()
    }
  },
  methods: {
    showModal () {
      this.$bus.$emit('modal-show', 'modal-shop-shipping')
    },
    isShopShippingMethod (shippingMethod) {
      if (shippingMethod === 'freeshipping') {
        return this.availableShops.length
      }
      return true
    }
  },
  beforeMount () {
    if (this.getCurrentProduct.final_price > 300) {
      [
        'nova_poshta_to_warehouse',
        'nova_poshta_to_door',
        'justin_to_department'
      ].map(deliveryType => this.shippingTypes[deliveryType].price.free = true)
    }
  },
  async mounted () {
    await this.$store.dispatch('checkoutPage/getShops', { city: this.city })
  },
  watch: {
    'city': function () {
      this.$store.dispatch('checkoutPage/getShops', { city: this.city })
    }
  }
}
</script>

