<template>
  <a :href="resource" target="_blank" class="instruction">
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
      <rect width="24" height="24" fill="url(#pattern1)"/>
      <defs>
        <pattern id="pattern1" patternContentUnits="objectBoundingBox" width="1" height="1">
          <use xlink:href="#image1" transform="scale(0.00833333)"/>
        </pattern>
        <image id="image1" width="120" height="120" xlink:href="data:image/png;base64,"/>
      </defs>
    </svg>
    <span class="text font">Інструкція на українській</span>
    <div class="format font">*.{{fileFormat}}</div>
  </a>
</template>

<script>
import ProductMixin from '../Mixins/ProductMixin';
export default {
  mixins: [ProductMixin],
  data: () => ({
    formatTypes: {
      img: {
        formats: ['jpg', 'png', 'svg']
      },
      file: {
        formats: ['pdf']
      }
    }
  }),
  computed: {
    resource () {
      const type = Object.keys(this.formatTypes).find(type => this.formatTypes[type].formats.includes(this.fileFormat))
      return type ? (type === 'img' ? this.image : this.file) : '';
    },
    fileFormat () {
      return this.getCurrentProduct.instruction.match(/\w+$/g)[0]
    },
    image () {
      return this.getThumbnail(this.getCurrentProduct.instruction, 0, 0, 'instruction')
    },
    file () {
      return this.getMediaFile(this.getCurrentProduct.instruction, 'instruction')
    }
  }
}
</script>

